<?php

class Customify_Dashboard {
    public $title;
    public $config;
    function __construct()
    {
        $this->title = __( 'Customify Options', 'customify' );
        add_action( 'admin_menu', array( $this, 'add_menu' ) );
        add_action( 'admin_enqueue_scripts', array( $this, 'scripts' ) );
    }
    function add_menu(){
        add_theme_page(
            $this->title,
            $this->title,
            'manage_options',
            'customify',
            array( $this, 'page' )
        );
    }

    function scripts( $id ){
       if ( $id != 'appearance_page_customify' ) {
           return ;
       }
        wp_enqueue_style( 'customify-admin',  get_template_directory_uri() . '/assets/css/admin/dashboard.css', false, Customify::$version );
    }

    function setup(){
        $theme = wp_get_theme();
        $this->config = array(
            'name' => $theme->get('Name'),
            'theme_uri' => $theme->get('ThemeURI'),
            'desc' => $theme->get('Description'),
            'author' => $theme->get('Author'),
            'author_uri' => $theme->get('AuthorURI'),
            'version' => $theme->get('Version'),
        );
    }

    function page(){
        $this->setup();
        $this->page_header();
        echo '<div class="wrap">';
        $this->page_inner();
        echo '</div>';
    }

